import React, { useState } from "react";

function CreateArea() {
    const [inputText,setInputText]=useState({
        title:"",
        content:""
    })
    function handleInput(event){
        const {name,value} = event.target;
        setInputText((prev)=>{
            if(name==="title"){
                return{
                    title:value,
                    content:prev.content
                }
            }else if(name==="content"){
                return{
                    title:prev.title,
                    content:value
                }
            }
            
        })
    }
    return (
        <div>
            <form>
                <p>{inputText.title}{inputText.content}</p>
                <input onChange={handleInput} name="title" placeholder="Title" value={inputText.title}/>
                <textarea onChange={handleInput} name="content" placeholder="Take a note..." rows="3" value={inputText.content} />
                <button>Add</button>
            </form>
        </div>
    );
}

export default CreateArea;
